import { useState } from "react";
import Logo from "./Logo";
import Form from "./Form";
import { PackingList } from "./PackingList";

export default function App() {
  const [items, setItems] = useState([]);

  function handelItemAddOn(item) {
    setItems((items) => [...items, item]);
  }

  function handelItemDelete(id) {
    console.log(id);
    setItems((items) => items.filter((item) => item.id !== id)); // filter out the item with the id
  }

  function handelToogleItem(id) {
    setItems((items) =>
      items.map((item) =>
        item.id === id ? { ...item, packed: !item.packed } : item
      )
    );
  }

  function handelClearItems() {
    const confirmed = window.confirm("Are you Sure want to delete all items?");
    if (confirmed) setItems([]);
  }

  return (
    <div className="app">
      <Logo />
      <Form onAddItems={handelItemAddOn} />
      <PackingList
        items={items}
        onDeleteItems={handelItemDelete}
        handelToogleItem={handelToogleItem}
        handelClearItems={handelClearItems}
      />
      <Stats items={items} />
    </div>
  );
}

export function Items({ items, onDeleteItems, handelToogleItem }) {
  return (
    <li>
      <input
        type="checkbox"
        value={items.packed}
        onChange={() => handelToogleItem(items.id)}
      ></input>
      <span style={items.packed ? { textDecoration: "line-through" } : {}}>
        {items.description} ({items.quantity})
      </span>
      <button onClick={() => onDeleteItems(items.id)}>‚ùå</button>
    </li>
  );
}

function Stats({ items }) {
  if (!items.length)
    return (
      <p className="stats">
        <em>Start Adding some items to Your List üöÄ</em>
      </p>
    );

  const totalItems = items.length;
  const packedItems = items.filter((item) => item.packed).length;
  const percentage = Math.round((packedItems / totalItems) * 100);
  return (
    <footer className="stats">
      <em>
        {percentage === 100
          ? "You Got Everything! Ready to go ‚úàÔ∏è"
          : `You Have ${totalItems} Items on your list. And You Have Already packed${" "}
        ${packedItems} items (${percentage}%)`}
      </em>
    </footer>
  );
}
