import { useState } from "react";

const initialItems = [
  { id: 1, description: "Passports", quantity: 2, packed: false },
  { id: 2, description: "Socks", quantity: 12, packed: false },
  { id: 3, description: "Pen", quantity: 2, packed: true },
  { id: 4, description: "Snacks", quantity: 20, packed: false },
];

export default function App() {
  return (
    <div className="app">
      <Logo />
      <Form />
      <PackingList />
      <Stats />
    </div>
  );
}

function Logo() {
  return <h1>üå¥Far Away üëú</h1>;
}
function Form() {
  const [description, setDescription] = useState("");

  return (
    <form className="add-form">
      <h3>What do you need for your üòç trip?</h3>
      <select>
        {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
          <option value={num} key={num}>
            {num}
          </option>
        ))}
      </select>
      <input type="text" placeholder="Items.." value={description} onChange={{}}></input>
      <button>Add</button>
    </form>
  );
}
function PackingList() {
  return (
    <div className="list">
      <ul>
        {initialItems.map((itms) => (
          <Items items={itms} key={itms.id} />
        ))}
      </ul>
    </div>
  );
}

function Items({ items }) {
  return (
    <li>
      <span style={items.packed ? { textDecoration: "line-through" } : {}}>
        {items.description} ({items.quantity})
      </span>
      <button>‚ùå</button>
    </li>
  );
}

function Stats() {
  return (
    <footer className="stats">
      <em>you Have X items on your list. and You have Y items checked off</em>
    </footer>
  );
}
