import { useState } from "react";



export default function App() {
  const [items, setItems] = useState([]);

  function handelItemAddOn(item) {
    setItems((items) => [...items, item]);
  }

  function handelItemDelete(id) {
    console.log(id);
    setItems((items) => items.filter((item) => item.id !== id)); // filter out the item with the id
  }

  return (
    <div className="app">
      <Logo />
      <Form onAddItems={handelItemAddOn} />
      <PackingList items={items} onDeleteItems={handelItemDelete} />
      <Stats />
    </div>
  );
}

function Logo() {
  return <h1>üå¥Far Away üëú</h1>;
}
function Form({ onAddItems }) {
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState(1);

  function handelSumbit(e) {
    e.preventDefault();

    if (!description) return;
    const newData = { description, quantity, packed: false, id: Date.now() };
    console.log(newData);

    onAddItems(newData);

    setDescription("");
    setQuantity(1);
  }

  return (
    <form className="add-form" onSubmit={handelSumbit}>
      <h3>What do you need for your üòç trip?</h3>
      <select
        value={quantity}
        onChange={(q) => setQuantity(Number(q.target.value))}
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
          <option value={num} key={num}>
            {num}
          </option>
        ))}
      </select>
      <input
        type="text"
        placeholder="Items.."
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      ></input>
      <button>Add</button>
    </form>
  );
}
function PackingList({ items, onDeleteItems }) {
  return (
    <div className="list">
      <ul>
        {items.map((itms) => (
          <Items items={itms} key={itms.id} onDeleteItems={onDeleteItems} />
        ))}
      </ul>
    </div>
  );
}

function Items({ items, onDeleteItems }) {
  return (
    <li>
      <input type="checkbox"></input>
      <span style={items.packed ? { textDecoration: "line-through" } : {}}>
        {items.description} ({items.quantity})
      </span>
      <button onClick={() => onDeleteItems(items.id)}>‚ùå</button>
    </li>
  );
}

function Stats() {
  return (
    <footer className="stats">
      <em>you Have X items on your list. and You have Y items checked off</em>
    </footer>
  );
}
