import { useState } from "react";
import { Logo } from "./Logo";

export default function App() {
  const [items, setItems] = useState([]);

  function handelItemAddOn(item) {
    setItems((items) => [...items, item]);
  }

  function handelItemDelete(id) {
    console.log(id);
    setItems((items) => items.filter((item) => item.id !== id)); // filter out the item with the id
  }

  function handelToogleItem(id) {
    setItems((items) =>
      items.map((item) =>
        item.id === id ? { ...item, packed: !item.packed } : item
      )
    );
  }

  function handelClearItems() {
    const confirmed = window.confirm("Are you Sure want to delete all items?");
    if (confirmed) setItems([]);
  }

  return (
    <div className="app">
      <Logo />
      <Form onAddItems={handelItemAddOn} />
      <PackingList
        items={items}
        onDeleteItems={handelItemDelete}
        handelToogleItem={handelToogleItem}
        handelClearItems={handelClearItems}
      />
      <Stats items={items} />
    </div>
  );
}

function Form({ onAddItems }) {
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState(1);

  function handelSumbit(e) {
    e.preventDefault();

    if (!description) return;
    const newData = { description, quantity, packed: false, id: Date.now() };
    console.log(newData);

    onAddItems(newData);

    setDescription("");
    setQuantity(1);
  }

  return (
    <form className="add-form" onSubmit={handelSumbit}>
      <h3>What do you need for your üòç trip?</h3>
      <select
        value={quantity}
        onChange={(q) => setQuantity(Number(q.target.value))}
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
          <option value={num} key={num}>
            {num}
          </option>
        ))}
      </select>
      <input
        type="text"
        placeholder="Items.."
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      ></input>
      <button>Add</button>
    </form>
  );
}
function PackingList({
  items,
  onDeleteItems,
  handelToogleItem,
  handelClearItems,
}) {
  const [sortBy, setSortBy] = useState("input");

  let sortedItems;

  if (sortBy === "input") sortedItems = items;

  if (sortBy === "description")
    sortedItems = items
      .slice()
      .sort((a, b) => a.description.localeCompare(b.description));

  if (sortBy === "packed")
    sortedItems = items
      .slice()
      .sort((a, b) => Number(a.packed) - Number(b.packed));
  return (
    <div className="list">
      <ul>
        {sortedItems.map((itms) => (
          <Items
            items={itms}
            key={itms.id}
            onDeleteItems={onDeleteItems}
            handelToogleItem={handelToogleItem}
          />
        ))}
      </ul>
      <div>
        <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
          <option value="input">Sort items by input</option>
          <option value="description">Sort items by description</option>
          <option value="packed">Sort items by packed</option>
        </select>
        <button onClick={() => handelClearItems()}>Clear list</button>
      </div>
    </div>
  );
}

function Items({ items, onDeleteItems, handelToogleItem }) {
  return (
    <li>
      <input
        type="checkbox"
        value={items.packed}
        onChange={() => handelToogleItem(items.id)}
      ></input>
      <span style={items.packed ? { textDecoration: "line-through" } : {}}>
        {items.description} ({items.quantity})
      </span>
      <button onClick={() => onDeleteItems(items.id)}>‚ùå</button>
    </li>
  );
}

function Stats({ items }) {
  if (!items.length)
    return (
      <p className="stats">
        <em>Start Adding some items to Your List üöÄ</em>
      </p>
    );

  const totalItems = items.length;
  const packedItems = items.filter((item) => item.packed).length;
  const percentage = Math.round((packedItems / totalItems) * 100);
  return (
    <footer className="stats">
      <em>
        {percentage === 100
          ? "You Got Everything! Ready to go ‚úàÔ∏è"
          : `You Have ${totalItems} Items on your list. And You Have Already packed${" "}
        ${packedItems} items (${percentage}%)`}
      </em>
    </footer>
  );
}
